import { Component, OnInit } from '@angular/core';
import{Login} from'./models/login.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})

export class AppComponent implements OnInit {
  login!:Login[];
  mySnap!:Login;
  title = 'tp';
  ngOnInit(){
  this.login=[
    {
      title:'Archibald',
      description:'mon meilleur amis depuis tout petit!',
     imageUrl:'https://cdn.pixabay.com/photo/2015/05/31/16/03/teddy-bear-792273_1280.jpg',
    createdDate : new Date(),
     snaps:0,
    },
    {
      title:'three rock mountain',
      description:'endroit magnifique',
      imageUrl:'https://upload.wikimedia.org/wikipedia.org/wikipedia/commons/thumb/0/08/three_rock_Mountain_southern_Tr.jpg/2880px-three_rock_Mountain',
      createdDate :new Date(),
      snaps:0
    },
    {
      title:'un bon repas',
      description:'yummm c bon!',
      imageUrl:'https://wtop.com/wp-content/uploads/2020/06/HEALTHFRESH.jpg',
      createdDate :new Date(),
      snaps:0
    }
  ];

 }
} 
<app-login *ngFor="let login of Login" [Login]="login"></app-login>
