import { Component,Input } from '@angular/core';
import { Login } from '../models/login.model';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent  {
  @Input() 
  login!: Login;
  title!: string;
  description!: string;
  createdDate!: Date;
  snaps!: number ; 
  imageUrl!: string;
  buttonText!: string;
  ngOnInit(){
    this.title='Maram !!';
    this.description='Etudien_iset tozeur_DSI21';
    this.createdDate=new Date();
    this.snaps=22;
    this.imageUrl='https://cdn.pixabay.com/photo/2015/05/31/16/03/teddy-bear-79227381280.jpg' ;
    this.buttonText='oh snap!';
  }
  onSnap(){
    if(this.buttonText==="oh snap!")
    {
      this.snaps++;
      this.buttonText="oops,unsnap";
    }
    else{
      this.snaps--;
      this.buttonText="oh snap!"
    }

  }
}

<div class="login-card">
    <h2>{{login.title}}</h2>
    <p>Mis en ligne le {{login.createdDate}}</p>
    <img [src]="login.imageUrl" [alt]="login.title">
    <p>{{login.description}}</p>
    <p>

<button (click)="onSnap()">{{buttonText}}</button>
{{login.snaps}}
    </p>
</div>
<link rel="preconnect" href="https://fonts.googleapis.com">
<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
<link href="https://fonts.googleapis.com/css2?family=Roboto:wght@300;400;500&display=swap" rel="stylesheet">
